<?phpuse Illuminate\Support\Facades\Route;use App\Http\Controllers\NewsController;use App\Http\Controllers\FeedbackController;use App\Http\Controllers\Product\CartController;use App\Http\Controllers\Product\BrandController;use App\Http\Controllers\Product\OrderController;use App\Http\Controllers\Product\ModelsController;use App\Http\Controllers\Product\ReviewController;use App\Http\Controllers\Product\CommentController;use App\Http\Controllers\Product\SearchController;use App\Http\Controllers\Product\ProductController;use App\Http\Controllers\Product\CategoryController;use App\Http\Controllers\Product\ManufacturerController;Route::middleware('auth:sanctum')->group(function () {    Route::post('/reviews/products/{id}', [ReviewController::class, 'store']);    Route::prefix('news')->group(function () {        Route::post('{newsId}/comments', [CommentController::class, 'store']);    });    // Orders    Route::post('/orders/checkout', [OrderController::class, 'placeOrder']);    Route::post('/orders/without-cart', [OrderController::class, 'createOrderWithoutCart']);    Route::get('/orders',  [OrderController::class, 'getUserOrders']);});Route::get('/reviews/products/{id}', [ReviewController::class, 'index']);Route::get('news/{newsId}/comments', [CommentController::class, 'getComments']);//    Route::prefix('blogs')->group(function () {//       Route::get('/', [BlogController::class, 'showAllBlogPosts']);//      // Route::get('{slug}', [BlogController::class, 'showOneBlogPost']);//    });////CART, BUCKETRoute::get('cart/{id}', [CartController::class, 'getProductInCart']);Route::get('cart', [CartController::class, 'getCart']);Route::post('/add', [CartController::class, 'addToCartProducts']);Route::post('cart/remove/{type}/{id}', [CartController::class, 'cleanOneElementFromCart']);Route::post('cart/clear', [CartController::class, 'cleanCart']);Route::post('cart-sync', [CartController::class, 'syncCartWithFrontend']);Route::patch('cart/update-quantity', [CartController::class, 'updateQuantity']);////NEWSRoute::get('/news', [NewsController::class, 'showAllNews']);Route::get('/news/{news}', [NewsController::class, 'showNewsBySLug']);////BRANDSRoute::get('/brands',[BrandController::class, 'index']);Route::get('/brands/{slug}', [BrandController::class, 'show']);////MANUFACTURERSRoute::get('/manufacturers', [ManufacturerController::class, 'getAllManufacturers']);Route::get('/manufacturers-home', [ManufacturerController::class, 'getPartManufacturersInMainPage']);Route::get('/manufacturers/{slug}', [ManufacturerController::class, 'showManufacturerBySlug']);////MODELSRoute::get('/models', [ModelsController::class, 'index']);////FEEDBACKRoute::post('/feedback', [FeedbackController::class, 'store']);Route::post('/efeedback', [FeedbackController::class, 'send']);////CATEGORYRoute::get('/categories', [CategoryController::class, 'index']);Route::get('/categories/{slug}', [CategoryController::class, 'show']);////PRODUCTSRoute::get('/rims', [ProductController::class, 'showAllDisks']);Route::get('/tires', [ProductController::class, 'showAllTires']);Route::get('/products/{slug}', [ProductController::class, 'showProductBySlug']);Route::get('/best-selling', [ProductController::class, 'getBestSellingProducts']);Route::get('/products/filter', [ProductController::class, 'filter'])->name('api.products.filter');Route::get('/brands', [ProductController::class, 'getModelsByBrand']);Route::get('/years', [ProductController::class, 'getYearsByModel']);Route::get('/mod', [ProductController::class, 'getModificationsByModelAndYear']);Route::get('/options', [ProductController::class, 'getOptionsByModification']);Route::get('/search', [ProductController::class, 'searchDisks']);Route::get('/toptions', [ProductController::class, 'getOptionsByModificationTires']);//SEARCHRoute::get('/search', [SearchController::class, 'search']);